/*導入每日打卡資料(.ts)*/
func processImportRecords() {

	log_info.Info("開始插入資料到MongoDB")

	// 建立 channel 存放 DailyRecord型態資料
	chaneOfcheckInRecords := make(chan CheckInRecord)

	// 標記完成
	dones := make(chan struct{}, worker)

	// 假資料塞入到 channel
	go addDailyRecordForManyDays(chaneOfcheckInRecords)

	// 2021.05.14 下午從這裡開始看
	//log_info.Info("chaneOfcheckInRecords: ", chaneOfcheckInRecords)

	// log_info.WithFields(logrus.Fields{
	// 	"len(chaneOfcheckInRecords)": len(chaneOfcheckInRecords),
	// }).Info("確認初始資料量")

	// 建立DB連線
	session, err := mgo.Dial(config.MongodbServerIP)
	if err != nil {
		fmt.Println("打卡紀錄插入錯誤(連上DB錯誤)")

		log_err.WithFields(logrus.Fields{
			"err": err,
		}).Error("打卡紀錄插入錯誤(連上DB錯誤)")

		panic(err)
	}

	defer session.Close() //完成時結束 DB session

	collection := session.DB(config.DBName).C(config.Collection)
	// 將假資料 插入mongodb資料庫
	for i := 0; i < worker; i++ {
		go insertRecordsToDB(collection, chaneOfcheckInRecords, dones)
	}

	//等待完成
	awaitForCloseResult(dones)
	fmt.Println("打卡記錄資料插入完畢")
}

type CheckInRecord struct {
	Id              int       `json:"id"`              //ID
	Name            string    `json:"name"`            //中文姓名
	CheckInTime     string    `json:"check_in_time"`   //打卡時:分:秒
	Pic             string    `json:"pic"`             //頭像
	LeaveType       string    `json:"leave_type"`      //無、病、假
	Date            string    `json:"date"`            //打卡日
	Department      string    `json:"department"`      //部門
	Position        string    `json:"position"`        //職位
	DateTimeToday   time.Time `json:"dateTimeToday"`   //當日時間(time.Time)
	DateTimeCheckIn time.Time `json:"dateTimeCheckIn"` //打卡時間(time.Time)
}

